mapper.select:
SELECT effectPubTable.wx_id AS 'wxId', effectPubTable.wx_name AS 'wxName', IFNULL(tpr.article_total, 0) AS 'articleTotal', IFNULL(tpr.read_total, 0) AS 'readTotal', IFNULL(tpr.like_total, 0) AS 'likeTotal', IFNULL(tpr.read_average, 0) AS 'readAverage', IFNULL(tpr.like_average, 0) AS 'likeAverage', IFNULL(tpr.headline_read_average, 0) AS 'headlineReadAverage', IFNULL(tpr.headline_like_average, 0) AS 'headlineLikeAverage', IFNULL(tpr.impact_index, 0) AS 'impactIndex', #{dayType} AS 'dayType' FROM t_pub_rank tpr RIGHT JOIN ( SELECT tpa.id id, tpa.wx_id wx_id, tpa.wx_name wx_name FROM t_pub_account tpa
AND tpa.id in
#{pubAccountId}
) effectPubTable ON tpr.pub_account_id = effectPubTable.id AND tpr.day_type = #{dayType} GROUP BY effectPubTable.id
order by ${sortName} ${sortBy}, effectPubTable.wx_id ASC
order by impact_index DESC, effectPubTable.wx_id ASC

mapper.insert:
insert into t_pub_rank (catalog_id, pub_account_id, article_total, 
  message_total, read_total, like_total, 
  read_average, like_average, headline_read_average, 
  headline_like_average, impact_index, day_type
  )
values (#{catalogId,jdbcType=INTEGER}, #{pubAccountId,jdbcType=INTEGER}, #{articleTotal,jdbcType=INTEGER}, 
  #{messageTotal,jdbcType=INTEGER}, #{readTotal,jdbcType=INTEGER}, #{likeTotal,jdbcType=INTEGER}, 
  #{readAverage,jdbcType=REAL}, #{likeAverage,jdbcType=REAL}, #{headlineReadAverage,jdbcType=REAL}, 
  #{headlineLikeAverage,jdbcType=REAL}, #{impactIndex,jdbcType=REAL}, #{dayType,jdbcType=INTEGER}
  )
